{
  "objectives": [
    "Explain the functions, process, and product of budgeting",
    "Identify and address criticisms against traditional budgeting through improvements and alternatives",
    "Explain the purposes and principles of variance analysis",
    "Compute, report, and interpret variances including static, flexible, volume, and price/usage variances",
    "Differentiate between favorable and unfavorable variances and their impact on operating income"
  ],
  "concepts": [
    {
      "id": "budget_def",
      "name": "Budget",
      "definition": "A detailed document translating a plan of action into financial terms over a limited time horizon, specifying short-term objectives and resource requirements.",
      "category": "Fundamentals"
    },
    {
      "id": "budgeting_functions",
      "name": "Functions of Budgeting",
      "definition": "The core purposes of budgeting including strategy formulation, resource allocation, delegation, coordination, and performance evaluation.",
      "category": "Fundamentals"
    },
    {
      "id": "participative_budgeting",
      "name": "Participative Budgeting",
      "definition": "An improvement to budgeting where managers at all levels are involved in the budget-setting process to increase commitment and accuracy.",
      "category": "Budgeting Improvements"
    },
    {
      "id": "zero_based_budgeting",
      "name": "Zero-Based Budgeting (ZBB)",
      "definition": "A budgeting method where every expense must be justified for each new period, starting from a <i>zero base</i>.",
      "category": "Budgeting Improvements"
    },
    {
      "id": "beyond_budgeting",
      "name": "Beyond Budgeting",
      "definition": "A management model that suggests abandoning traditional budgets in favor of more adaptive and decentralized goals.",
      "category": "Alternatives"
    },
    {
      "id": "variance_analysis",
      "name": "Variance Analysis",
      "definition": "The systematic comparison of actual results with planned results to assess the impact on operating income.",
      "category": "Control"
    },
    {
      "id": "static_budget_variance",
      "name": "Static Budget Variance",
      "definition": "The difference between actual operating income and the original budgeted operating income: \\(OI_a - OI_b\\).",
      "category": "Variance Types"
    },
    {
      "id": "flexible_budget_variance",
      "name": "Flexible Budget Variance",
      "definition": "The difference between actual results and the flexible budget (budgeted amounts adjusted for actual volume): \\(Q_a \\times (UCM_a - UCM_b) - (FC_a - FC_b)\\).",
      "category": "Variance Types"
    },
    {
      "id": "volume_variance",
      "name": "Volume Variance",
      "definition": "The difference between the flexible budget and the static budget, calculated as: \\((Q_a - Q_b) \\times UCM_b\\).",
      "category": "Variance Types"
    },
    {
      "id": "favorable_variance",
      "name": "Favorable Variance (F)",
      "definition": "A variance that increases profit relative to the budgeted amount.",
      "category": "Reporting"
    },
    {
      "id": "unfavorable_variance",
      "name": "Unfavorable Variance (U)",
      "definition": "A variance that decreases profit relative to the budgeted amount.",
      "category": "Reporting"
    },
    {
      "id": "standard_transfer_price",
      "name": "Standard Transfer Price",
      "definition": "A predetermined price for internal transactions used to avoid penalizing buying divisions for the inefficiencies of selling divisions.",
      "category": "Internal Control"
    }
  ],
